import React, {Component} from 'react' 
import { AUTHOR_TITLE, AUTHOR_SUBTITLE, AUTHORS } from '../constants/authorconstants/authorconstants';
import Section from '../baseComponents/sectionTitle';
import AuthorInfo from './authorInfo';

export default class AuthorView extends Component {
    constructor() {
        super();

        this.state = {
            persons: AUTHORS
        };
        this.renderPerson = this.renderPerson.bind(this);
    }

    renderQuestion(key) {
        return  <AuthorInfo
        key={key} 
        index={key}
        details={this.state.persons[key]}
        />
      }

    renderPerson(key) {
        return (
            <div className="bg-light py-5">
                <div className="container">
                    <div className="row">
                        <div className="col">

                            <Section
                                Title={AUTHOR_TITLE}
                                subTitle={AUTHOR_SUBTITLE}
                            /> 

                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
