import React, {Component} from 'react'
import Logo from '../../Images/logo_business.png'
import '../../style/header.scss'
import { Link, animateScroll as scroll } from "react-scroll"
import {
  Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem } from 'reactstrap';

export default class Header extends Component {
  constructor(props) {
    super(props);

    this.toggle = this.toggle.bind(this);
    this.state = {
      isOpen: false
    };
  }
  toggle() {
    this.setState({
      isOpen: !this.state.isOpen
    });
  }
  scrollToTop = () => {
    scroll.scrollToTop();
  };
    render() {
        return (
          <nav className="navbar navbar-expand-md navbar-light cursor py-4">
          <div className="container">
          <img
            src={Logo}
            className="nav-logo"
            alt="Logo"
            onClick={this.scrollToTop}
          />
           <NavbarToggler onClick={this.toggle} />
              <Collapse isOpen={this.state.isOpen} navbar>
          <div className="collapse navbar-collapse" id="navbarCollapse">
          <ul className="navbar-nav ml-auto">
            <li className="nav-item">
              <Link
                activeClass="active"
                to="homeSection"
                spy={true}
                smooth={true}
                offset={-70}
                duration={500}
              >
                Home
              </Link>
            </li>
            <li className="nav-item">
              <Link
                activeClass="active"
                to="newsletterSection"
                spy={true}
                smooth={true}
                offset={-70}
                duration={500}
              >
                Newsletter
              </Link>
            </li>
            <li className="nav-item">
              <Link
                activeClass="active"
                to="authorSection"
                spy={true}
                smooth={true}
                offset={-70}
                duration={500}
              >
                Authors
              </Link>
            </li>
            <li className="nav-item">
              <Link
                activeClass="active"
                to="contactSection"
                spy={true}
                smooth={true}
                offset={-70}
                duration={500}
              >
                Contact
              </Link>
            </li>
          </ul>
          </div>
          </Collapse>
          </div>

      </nav>



        )
    }
}
