import React from 'react'

export default function AuthorInfo({name, jobDescribtion, info}) {
    const person = this.props.details;
        return (
            <div className="col-md-3 my-5">
                <div className="card text-center border-info py-5">
                    <div className="card-body">
                        <h2 className="display-5">{person.name}</h2>
                        <h5 className="lead">{person.jobDescribtion}</h5>
                        <p>{person.info}</p>
                    </div>
                </div>
            </div>
        )
    }
