import React, {Component} from 'react'
import Logo from '../../Images/logo_business.png'
import '../../style/header.scss'
import { Link, animateScroll as scroll } from "react-scroll";

export default class Header extends Component {
  scrollToTop = () => {
    scroll.scrollToTop();
  };
    render() {
        return (
          <nav className="navbar navbar-expand-md navbar-lightpy-4" id="main-nav">
          <div className="container">
          <img
            src={Logo}
            className="navbar-brand"
            alt="Logo"
            onClick={this.scrollToTop}
          />
          <button className="navbar-toggler" data-toggle="collapase" data-target="#navbarCollapse">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarCollapse">
          <ul className="navbar-nav ml-auto">
            <li className="nav-item">
              <Link
                activeClass="active"
                to="homeSection"
                spy={true}
                smooth={true}
                offset={-70}
                duration={500}
              >
                Home
              </Link>
            </li>
            <li className="nav-item">
              <Link
                activeClass="active"
                to="aboutSection"
                spy={true}
                smooth={true}
                offset={-70}
                duration={500}
              >
                About
              </Link>
            </li>
            <li className="nav-item">
              <Link
                activeClass="active"
                to="authorSection"
                spy={true}
                smooth={true}
                offset={-70}
                duration={500}
              >
                Authors
              </Link>
            </li>
            <li className="nav-item">
              <Link
                activeClass="active"
                to="contactSection"
                spy={true}
                smooth={true}
                offset={-70}
                duration={500}
              >
                Contact
              </Link>
            </li>
          </ul>
          </div>
        </div>
      </nav>
        )
    }
}
