import React, {Component} from 'react' 
import { AUTHOR_TITLE, AUTHOR_SUBTITLE, AUTHORS } from '../constants/authorconstants/authorconstants';
import Section from '../baseComponents/sectionTitle';
import AuthorInfo from './authorInfo';

export default class AuthorView extends Component {
    constructor() {
        super();

        this.state = {
            persons: AUTHORS
        }

    };

    render() {

        return (
            <div className="bg-light py-5">
                <div className="container">
                    <div className="row">
                        <div className="col">
                            <Section
                                Title={AUTHOR_TITLE}
                                subTitle={AUTHOR_SUBTITLE}
                            /> 
                            <AuthorInfo
                            name="Brock Blount"
                            jobDescribtion="Technical Consultant"
                            info="Lorem ipsum dolor sit amet consectetur adipisicing elit. Sed commodi nostrum, ab libero voluptas officia."
                            />
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
